const setTodoListByFilter = (todos, filter) => {
  switch(filter) {
    case 'done':
      console.log('done')
      return todos.filter(todo => todo.isDone);
    case 'active':
      console.log('active')
      return todos.filter(todo => !todo.isDone);
    default:
      console.log('all')
      return todos
  }
};

module.exports = {
  setTodoListByFilter
};
