import { SET_FILTER, CLEAR_FILTER } from '../actions/filter';

export const filter = (state = [], action) => {
  switch (action.type) {
    case SET_FILTER:
      return action.type;
    case CLEAR_FILTER:
      return [];
    default:
      return state;
  }
};
