export const ADD_TODOS = 'cx-ray/TODOS/ADD';

export function addObjective(objective, employeeId) {
  return async dispatch => {
    service.post(`/employees/${employeeId}/objectives`, objective)
      .then(() => dispatch(fetchObjectives(employeeId)))
      .catch(error => {
        dispatch({
          type: ADDING_OBJECTIVE_FAILED,
          payload: error
        });
      });
  };
};
