const setTodoListByFilter = (todos, filter) => {
  switch(filter[0]) {
    case 'done':
      console.log('done', filter)
      return todos.filter(todo => todo.isDone);
    case 'active':
      console.log('active', filter)
      return todos.filter(todo => !todo.isDone);
    default:
      console.log('all', filter)
      return todos
  }
};

module.exports = {
  setTodoListByFilter
};
