export const SET_FILTER = 'cx-ray/FILTER/SET';
export const CLEAR_FILTER = 'cx-ray/FILTER/CLEAR';

export function setFilter(filter) {
  return dispatch => {
    dispatch({
      type: SET_FILTER,
      payload: filter
    });
  };
};
