export const ADD_TODOS = 'cx-ray/TODOS/ADD';

export function addObjective(objective, employeeId) {
  return async dispatch => {
    service.post(`/employees/${employeeId}/objectives`, objective)
      .then(() => dispatch(fetchObjectives(employeeId)))
      .catch(error => {
        dispatch({
          type: ADDING_OBJECTIVE_FAILED,
          payload: error
        });
      });
  };
};

export function editObjective(objective, employeeId) {
  return async dispatch => {
    service.put(`/employees/${employeeId}/objectives/${objective.id}`, objective)
      .then(
        dispatch({
          type: EDIT_OBJECTIVE,
          payload: objective
        }))
      .catch(error => {
        dispatch({
          type: EDITING_OBJECTIVE_FAILED,
          payload: error
        });
      });
  };
};

export function removeObjective(objective, employeeId) {
  return async dispatch => {
    service.delete(`/employees/${employeeId}/objectives/${objective.objective_id}`)
      .then(
        dispatch({
          type: REMOVE_OBJECTIVE,
          payload: objective.objective_id
        })
      )
      .catch(error => {
        dispatch({
          type: DELETING_OBJECTIVE_FAILED,
          payload: error
        });
      });
  };
};
